import CliTable3 from 'cli-table3';
export const createTable = (options = {}) => {
    const defaultOptions = {
        style: { head: [], border: [], 'padding-left': 2, 'padding-right': 2 },
        chars: {
            mid: '·',
            'top-mid': '|',
            'left-mid': ' ·',
            'mid-mid': '|',
            'right-mid': '·',
            left: ' |',
            right: '|',
            'top-left': ' ·',
            'top-right': '·',
            'bottom-left': ' ·',
            'bottom-right': '·',
            middle: '·',
            top: '-',
            bottom: '-',
            'bottom-mid': '|',
        },
    };
    return new CliTable3({ ...defaultOptions, ...options });
};
