import { TASK_SOLIDSTATE_DONATE } from '../task_names.js';
import { task } from 'hardhat/config';
import { ArgumentType } from 'hardhat/types/arguments';
export default task(TASK_SOLIDSTATE_DONATE)
    .setDescription('Make a donation to Solidstate')
    .addPositionalArgument({
    name: 'amount',
    description: 'Quantity of tokens to send (formatted with 18 decimals)',
    type: ArgumentType.BIGINT,
})
    .setAction(() => import('../actions/solidstate_donate.js'))
    .build();
