import { getSolidstateHardhatPlugins } from '../lib/packages.js';
import { printSolidstatePluginStatus } from '../lib/print.js';
import type { NewTaskActionFunction } from 'hardhat/types/tasks';

interface TaskActionArguments {}

const action: NewTaskActionFunction<TaskActionArguments> = async (
  args,
  hre,
) => {
  const solidstateHardhatPlugins = await getSolidstateHardhatPlugins(hre);
  printSolidstatePluginStatus(solidstateHardhatPlugins);
};

export default action;
