import chalk from 'chalk';

// Solidstate brand colors
const emerald = '#22D372';
const canary = '#FDF685';
const darkBlue = '#092F44';

// ASCII art banner generated here:
// https://textkool.com/en/ascii-art-generator?hl=default&vl=default&font=ANSI%20Shadow&text=Solidstate
/*                         <-- 80 character width -->                         */
let banner = `\
                                                                                
 ███████╗ ██████╗ ██╗     ██╗██████╗ ███████╗████████╗ █████╗ ████████╗███████╗ 
 ██╔════╝██╔═══██╗██║     ██║██╔══██╗██╔════╝╚══██╔══╝██╔══██╗╚══██╔══╝██╔════╝ 
 ███████╗██║   ██║██║     ██║██║  ██║███████╗   ██║   ███████║   ██║   █████╗   
 ╚════██║██║   ██║██║     ██║██║  ██║╚════██║   ██║   ██╔══██║   ██║   ██╔══╝   
 ███████║╚██████╔╝███████╗██║██████╔╝███████║   ██║   ██║  ██║   ██║   ███████╗ 
 ╚══════╝ ╚═════╝ ╚══════╝╚═╝╚═════╝ ╚══════╝   ╚═╝   ╚═╝  ╚═╝   ╚═╝   ╚══════╝ 
                                                                                \
`;

// colorize
banner = chalk.hex(canary).bgHex(darkBlue)(
  banner.replaceAll('█', chalk.hex(emerald)('█')),
);

// apply ANSI no-wrap escape sequences
banner = `\x1b[?7l${banner}\x1b[?7h`;

export default banner;
